mapper.select:
SELECT * FROM food LEFT OUTER JOIN (select * from food_attach where thumbnail = 'Y') D ON food.fNo = D.fNo WHERE food.status = 'Y'
AND food_name like concat('%', #{searchValue}, '%')
AND food_address like concat('%', #{searchValue}, '%')
AND LType = #{type}
order by food.fNo desc LIMIT #{sNum},#{perPageNum}

mapper.insert:

SELECT LAST_INSERT_ID()

INSERT INTO
food(
	uNo
	,food_name
	,food_address
	,food_phone
	,food_food_kind
	,food_avg_price
	,food_write_date
	,food_working_hours
	,food_holiday
	,food_website
	,food_content
	,LType
)VALUES(
	#{uNo}
	,#{food_name}
	,#{food_address}
	,#{food_phone}
	,#{food_food_kind}
	,#{food_avg_price}
	,#{food_write_date}
	,#{food_working_hours}
	,#{food_holiday}
	,#{food_website}
	,#{food_content}
	,#{LType}
)

mapper.update:
UPDATE food
   SET food_name = #{food_name} 
     , food_address = #{food_address}
     , food_phone = #{food_phone}
     , food_food_kind = #{food_food_kind}
     , food_avg_price = #{food_avg_price}
     , food_write_date = #{food_write_date}
     , food_working_hours = #{food_working_hours}
     , food_holiday = #{food_holiday}
     , food_website = #{food_website}
     , food_content = #{food_content}
     , LType = #{LType}
 WHERE fNo = #{fNo}

mapper.insert:
INSERT INTO
food_menu(
	fNo
	,food_menu_name
	,food_menu_price
)VALUES(
	#{fNo}
	,#{food_menu_name}
	,#{food_menu_price}
)

	SELECT MAX(food_menu_number)
  	   FROM food_menu

mapper.update:
UPDATE food_attach SET
	thumbnail = 'N'
WHERE fNo = #{fNo}

UPDATE food_attach SET
	thumbnail = 'Y'
WHERE food_attach_number = #{food_attach_number}

mapper.insert:

	SELECT LAST_INSERT_ID()

INSERT INTO food_review(
	fNo
	,uNo
	,food_review_content
) VALUES (
	#{fNo},
	#{uNo},
	#{food_review_content}
)

mapper.select:
SELECT * ,(select uNick from User WHERE uNo = food_review.uNo) as uNick
FROM food_review
 WHERE food_review_number = #{food_review_number}

mapper.update:
UPDATE food_review
	SET food_review_content = #{food_review_content}
	WHERE food_review_number = #{food_review_number}
